mapper.select:
select t.* from ( select t9.*, count(distinct t10.id) + count(t11.commentid) commentNums from( select t6.*, count(t8.blogid) viewNums from ( select t1.id id, t1.blogid blogId, t2.loginid userId, t2.name userName, t3.columnid columnId, t3.name columnName, t1.title title, t1.type type, t1.enable enable, t1.content content, t4.url cover, t1.createtime createTime, count(t5.blogid) collectionNums from blog t1 left join blog_user t2 on t1.userid= t2.id left join yeblog_db.column t3 on t1.columnid= t3.id left join sys_file t4 on t1.cover= t4.id left join user_collection t5 on t1.id= t5.blogid group by t1.id) t6 left join( select t7.* from blog_view t7 group by t7.ip, day(t7.time) ) t8 on t6.id= t8.blogid group by t6.blogId ) t9 left join blog_comment t10 on t9.id= t10.blogid left join comment_reply t11 on t10.id= t11.commentid group by t9.id order by t9.createtime desc ) t where 1=1
and t.title like (concat('%',#{title},'%'))
and t.blogId like (concat('%',#{blogId},'%'))
and t.userId like (concat('%',#{userId},'%'))
and t.enable = #{enable}
and t.createTime >= date_format(#{createTimeQ},'%Y-%m-%d')
and t.createTime <= date_format(#{createTimeZ},'%Y-%m-%d')
and t.type in
#{type}
and t.columnId in
#{columnId}
and t.collectionNums >= #{collectionNumsQ}
and t.collectionNums <= #{collectionNumsZ}
and t.viewNums >= #{viewNumsQ}
and t.viewNums <= #{viewNumsZ}
and t.commentNums >= #{commentNumsQ}
and t.commentNums <= #{commentNumsZ}
