select group_concat(t.tjny) tjny, group_concat(t.value) value, "全部" type from ( select t1.day tjny, ifnull(t2.value, 0) value from ( select DATE_FORMAT(@cdate := DATE_ADD(@cdate, INTERVAL -1 year), '%Y年') day from( select @cdate := DATE_ADD(sysdate(), INTERVAL+ 1 year) from blog ) t0 limit 5 ) t1 left join ( select count(*) value, DATE_FORMAT(t.createtime, '%Y年') tjny from blog t where 1= 1
and DATE_FORMAT(t.createtime,'%Y') >= #{tjnyq}
and DATE_FORMAT(t.createtime,'%Y') <= #{tjnyz}
group by DATE_FORMAT(createtime, '%Y') ) t2 on t1.day= t2.tjny order by t1.day ) t union select group_concat(t.tjny) tjny, group_concat(t.value) value, "原创" type from ( select t1.day tjny, ifnull(t2.value, 0) value from ( select DATE_FORMAT(@cdate := DATE_ADD(@cdate, INTERVAL -1 year), '%Y年') day from( select @cdate := DATE_ADD(sysdate(), INTERVAL+ 1 year) from blog ) t0 limit 5 ) t1 left join ( select count(*) value, DATE_FORMAT(t.createtime, '%Y年') tjny from blog t where 1= 1 and t.type= "0"
and DATE_FORMAT(t.createtime,'%Y') >= #{tjnyq}
and DATE_FORMAT(t.createtime,'%Y') <= #{tjnyz}
group by DATE_FORMAT(createtime, '%Y') ) t2 on t1.day= t2.tjny order by t1.day ) t union select group_concat(t.tjny) tjny, group_concat(t.value) value, "转载" type from ( select t1.day tjny, ifnull(t2.value, 0) value from ( select DATE_FORMAT(@cdate := DATE_ADD(@cdate, INTERVAL -1 year), '%Y年') day from( select @cdate := DATE_ADD(sysdate(), INTERVAL+ 1 year) from blog ) t0 limit 5 ) t1 left join ( select count(*) value, DATE_FORMAT(t.createtime, '%Y年') tjny from blog t where 1= 1 and t.type= "1"
and DATE_FORMAT(t.createtime,'%Y') >= #{tjnyq}
and DATE_FORMAT(t.createtime,'%Y') <= #{tjnyz}
group by DATE_FORMAT(createtime, '%Y') ) t2 on t1.day= t2.tjny order by t1.day ) t union select group_concat(t.tjny) tjny, group_concat(t.value) value, "草稿" type from ( select t1.day tjny, ifnull(t2.value, 0) value from ( select DATE_FORMAT(@cdate := DATE_ADD(@cdate, INTERVAL -1 year), '%Y年') day from( select @cdate := DATE_ADD(sysdate(), INTERVAL+ 1 year) from blog ) t0 limit 5 ) t1 left join ( select count(*) value, DATE_FORMAT(t.createtime, '%Y年') tjny from blog t where 1= 1 and t.type= "2"
and DATE_FORMAT(t.createtime,'%Y') >= #{tjnyq}
and DATE_FORMAT(t.createtime,'%Y') <= #{tjnyz}
group by DATE_FORMAT(createtime, '%Y') ) t2 on t1.day= t2.tjny order by t1.day ) t

