select count(t1.id) value, concat( ifnull(t2.name,''), '(', (case when t1.type= "0" then "原创" when t1.type= "1" then "转载" when t1.type= "2" then "草稿" end), ')' ) name from blog t1 left join yeblog_db.column t2 on t1.columnid= t2.id where t1.enable= "1"
and t2.columnid in
#{columnId}
group by t1.columnid,t1.type;
